pageContext.rootQuery:
query($uid: String!) {
  prismic {
    page_detail(lang: "en-gb", uid: $uid) {
      _meta {
        uid
        id
        type
      }
      body {
        ... on PRISMIC_Page_detailBodyNugget_hero_banner {
          type
          primary {
            nugget_hero_title
            nugget_hero_subheader
            nugget_hero_animation
            nugget_hero_background_image
          }
        }
        ... on PRISMIC_Page_detailBodyNugget_feature {
          type
          primary {
            nugget_scenario_header
            nugget_scenario_caveat
          }
          fields {
            nugget_feature_step_description
            nugget_feature_step_image
            nugget_feature_step_title
            nugget_feature_step_variant
          }
        }
        ... on PRISMIC_Page_detailBodyNugget_summary {
          type
          primary {
            nugget_summary_header
            nugget_summary_caveat
          }
          fields {
            fact_tile_body
          }
        }
        ... on PRISMIC_Page_detailBodyCta_bar {
          type
          primary {
            nugget_cta_body
            nugget_cta_button_copy
            nugget_cta_button_href {
              _linkType
              ... on PRISMIC__ExternalLink {
                _linkType
                url
              }
            }
            nugget_cta_variant
            nugget_cta_caveat
          }
        }
        ... on PRISMIC_Page_detailBodyInteractive_map {
          type
          primary {
            nugget_interactive_map_header
            nugget_interactive_map_url
            nugget_interactive_map_title
            nugget_interactive_map_footer
          }
        }
        ... on PRISMIC_Page_detailBodyNugget_detail_block {
          type
          primary {
            nugget_detail_block_body
            nugget_detail_block_section_header
            nugget_detail_block_standfirst
            nugget_detail_block_caveat
          }
        }
        ... on PRISMIC_Page_detailBodyCalculators {
          type
          label
          primary {
            calculator_section_subheader
            calculator_section_header
          }
          fields {
            calculator_option_heading
            calculator_option_label
            calculator_caveat
          }
        }
        ... on PRISMIC_Page_detailBodyDefinition_row {
          type
          primary {
            nugget_definition_row_section_header
            nugget_definition_row_section_subheader
            nugget_definition_row_footer
          }
          fields {
            nugget_definition_tile_image
            nugget_definition_tile_list
            nugget_definition_tile_title
          }
        }
        ... on PRISMIC_Page_detailBodyVideo {
          type
          primary {
            nugget_video_section_header
            nugget_video_section_subheader
            nugget_video_embed_url
            youtube_video_id
            youtube_video_title
          }
        }
        ... on PRISMIC_Page_detailBodyComparison_tables {
          type
          primary {
            nugget_comparison_tables_section_header
            nugget_comparison_tables_section_subheader
            nugget_comparison_tables_footer
          }
          fields {
            nugget_comparison_table_caption
            nugget_comparison_table_header_row_column_1
            nugget_comparison_table_header_row_column_2
            nugget_comparison_table_body_row_1_cell_1
            nugget_comparison_table_body_row_1_cell_2
            nugget_comparison_table_body_row_1_cell_3
            nugget_comparison_table_body_row_2_cell_1
            nugget_comparison_table_body_row_2_cell_2
            nugget_comparison_table_body_row_2_cell_3
          }
        }
        ... on PRISMIC_Page_detailBodyFormula {
          type
          primary {
            nugget_formula_section_header
            nugget_formula_section_subheader
            nugget_formula_calculation_part_1
            nugget_formula_calculation_part_2
            nugget_formula_calculation_result
            nugget_formula_footer
            nugget_formula_cta_text
            nugget_formula_cta_href
          }
        }
        ... on PRISMIC_Page_detailBodyComparison_graph {
          type
          primary {
            nugget_comparison_graph_section_header
            nugget_comparison_graph_section_subheader
            nugget_comparison_graph_input_instruction
            nugget_comparison_graph_title
            nugget_comparison_graph_footer_header
            nugget_comparison_graph_footer_body
          }
        }
        ... on PRISMIC_Page_detailBodyOptions_block {
          type
          primary {
            options_block_section_header
            options_block_section_subheader
          }
          fields {
            options_block_option_label
            options_block_option_heading
            options_block_option_list
          }
        }
        __typename
        ... on PRISMIC_Page_detailBodyRelated_nuggets {
          type
          label
          primary {
            cta_view_all_nugget
            title
            subtitle
          }
          fields {
            nugget {
              ... on PRISMIC_Page_detail {
                _meta {
                  uid
                }
                bucket {
                  ... on PRISMIC_Nugget_bucket {
                    name
                    rank
                    colour
                  }
                }
                category
                overview_headline
              }
            }
          }
        }
      }
      bucket {
        ... on PRISMIC_Nugget_bucket {
          colour
          name
          rank
          _meta {
            id
          }
        }
      }
      category
      overview_headline
      seo_title
      seo_description
    }
  }
}
